import React from 'react';
import {getMergeSortAnimations, getQuickSortAnimation} from '../sortingAlgorithms/sortingAlgorithms';
import './SortingVisualizer.css';

// Change this value for the speed of the animations.
const ANIMATION_SPEED_MS = 3;

// This is the main color of the array bars.
const PRIMARY_COLOR = '#ff0080';

// This is the color of array bars that are being compared throughout the animations.
const SECONDARY_COLOR = '#00FF7F';

export default class SortingVisualizer extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            array: [],
            height: getWindowDimensions('height') * .8,
            width: getWindowDimensions('width')/7.9,
            showMenu: false,
        }

        this.showMenu = this.showMenu.bind(this);

    }
    componentDidMount(){
        this.resetArray();
    }

    resetArray(){
        const array = [];
        for(let i =0; i < this.state.width; i++){
            array.push(randomIntFromInterval(5, this.state.height));
        }
        this.setState({array});
    }

    mergeSort() {
        const animations = getMergeSortAnimations(this.state.array);
        console.log(animations)
        for (let i = 0; i < animations.length; i++) {
          const arrayBars = document.getElementsByClassName('array-bar');
          const isColorChange = i % 3 !== 2;
          if (isColorChange) {
            const [barOneIdx, barTwoIdx] = animations[i];
            const barOneStyle = arrayBars[barOneIdx].style;
            const barTwoStyle = arrayBars[barTwoIdx].style;
            const color = i % 3 === 0 ? SECONDARY_COLOR : PRIMARY_COLOR;
            setTimeout(() => {
              barOneStyle.backgroundColor = color;
              barTwoStyle.backgroundColor = color;
            }, i * ANIMATION_SPEED_MS);
          } else {
            setTimeout(() => {
              const [barOneIdx, newHeight] = animations[i];
              const barOneStyle = arrayBars[barOneIdx].style;
              barOneStyle.height = `${newHeight}px`;
            }, i * ANIMATION_SPEED_MS);
          }
        }
      }

    quickSort(){
        const animations = getQuickSortAnimation(this.state.array);
        console.log(animations)
        for (let i = 0; i < animations.length; i++) {
          const arrayBars = document.getElementsByClassName('array-bar');
          const isColorChange = i % 3 !== 2;
          if (isColorChange) {
            const [barOneIdx, barTwoIdx] = animations[i];
            const barOneStyle = arrayBars[barOneIdx].style;
            const barTwoStyle = arrayBars[barTwoIdx].style;
            const color = i % 3 === 0 ? SECONDARY_COLOR : PRIMARY_COLOR;
            setTimeout(() => {
              barOneStyle.backgroundColor = color;
              barTwoStyle.backgroundColor = color;
            }, i * ANIMATION_SPEED_MS);
          } else {
            setTimeout(() => {
              const [barOneIdx, barTwoIndex] = animations[i];
              const barOneStyle = arrayBars[barOneIdx].style;
              const barTwoStyle = arrayBars[barTwoIndex].style;
              barOneStyle.height = `${this.state.array[barTwoIndex]}px`;
              barTwoStyle.height = `${this.state.array[barOneIdx]}px`;
              barOneStyle.color = `${this.state.array[barTwoIndex]}px`;
              barTwoStyle.color = `PRIMARY_COLOR`;
            }, i * ANIMATION_SPEED_MS);
          }
        }
    }

    meapSort(){}

    bubbleSort(){}

    //open dropwdown
    showMenu(event) {
        event.preventDefault();
        
        if(this.state.showMenu){
            this.setState({
                showMenu: false,
              });
        }else{
            this.setState({
                showMenu: true,
              });
        }

        
      }

    render(){
        const {array} = this.state;

        return(
            <div className="array-container">
                <div className="menu">
                    <div><button onClick={()=>{this.resetArray()}} className="generateArray">New Array</button></div>
                    <div><button onClick={()=>{this.mergeSort()}} className="generateArray">Merge Sort</button></div>
                    <div><button onClick={() => {this.quickSort()}}>Quick Sort</button></div>
                    <div className="AlgoDropwDown">
                        <button onClick={this.showMenu} type="button">
                        Choose Algorithm
                        </button>
                        {
                        this.state.showMenu
                            ? (
                        <div className="dropdown-menu">
                            <button onClick={() => this.mergeSort()} type="button">Merge Sort</button>
                            <button onClick={() => {this.quickSort()}}>Quick Sort</button>
                            <button onClick={() => {this.meapSort()}}>Meap Sort</button>
                            <button onClick={() => {this.bubbleSort()}}>Bubble Sort</button>
                        </div>
                        )
                        : (
                          null
                        )}
                    </div>
                </div>
                {array.map((value, idx) => {
                    return <div className="array-bar" key={idx} style={{height: `${value}px`}}>

                    </div>
                })}
            </div>
        )
    }
}

function randomIntFromInterval(min, max){
    return Math.floor(Math.random() * (max - min + 1) + min);
}
function getWindowDimensions(whichOne) {
    const { innerWidth: width, innerHeight: height } = window;
    if(whichOne === 'height'){
        return height;
    }else{
        return width;
    }
  }
