import React from 'react';
import './SortingVisualizer.css';

export default class SortingVisualizer extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            array: [],
            height: getWindowDimensions('height') * .8,
            width: getWindowDimensions('width')/5.7,
            showMenu: false,
        }

        this.showMenu = this.showMenu.bind(this);
        this.closeMenu = this.closeMenu.bind(this);

    }
    componentDidMount(){
        this.resetArray();
    }

    resetArray(){
        const array = [];
        for(let i =0; i < this.state.width; i++){
            array.push(randomIntFromInterval(5, this.state.height));
        }
        this.setState({array});
    }

    //open dropwdown
    showMenu(event) {
        event.preventDefault();
        
        if(this.state.showMenu){

        }

        
      }

    //close dropdown
    closeMenu() {
        this.setState({ showMenu: false }, () => {
          document.removeEventListener('click', this.closeMenu);
        });
    }

    render(){
        const {array} = this.state;

        return(
            <div className="array-container">
                <div className="menu">
                    <button onClick={()=>{this.resetArray()}} className="generateArray">New Array</button>
                    <div>
                        <button onClick={this.showMenu}>
                        Show menu
                        </button>
                        {
                        this.state.showMenu
                            ? (
                        <div className="dropdown-menu">
                            <button> Menu item 1 </button>
                            <button> Menu item 2 </button>
                            <button> Menu item 3 </button>
                        </div>
                        )
                        : (
                          null
                        )}
                    </div>
                </div>
                {array.map((value, idx) => {
                    return <div className="array-bar" key={idx} style={{height: `${value}px`}}>

                    </div>
                })}
            </div>
        )
    }
}

function randomIntFromInterval(min, max){
    return Math.floor(Math.random() * (max - min + 1) + min);
}
function getWindowDimensions(whichOne) {
    const { innerWidth: width, innerHeight: height } = window;
    if(whichOne === 'height'){
        return height;
    }else{
        return width;
    }
  }
