import React from 'react';
import * as sortingAlgorithms from '../sortingAlgorithms/sortingAlgorithms';
import './SortingVisualizer.css';

export default class SortingVisualizer extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            array: [],
            height: getWindowDimensions('height') * .8,
            width: getWindowDimensions('width')/5.7,
            showMenu: false,
        }

        this.showMenu = this.showMenu.bind(this);

    }
    componentDidMount(){
        this.resetArray();
    }

    resetArray(){
        const array = [];
        for(let i =0; i < this.state.width; i++){
            array.push(randomIntFromInterval(5, this.state.height));
        }
        this.setState({array});
    }

    mergeSort(){
        let array = this.state.array;
        /*console.log(this.state.array.length)
        const sortedArray = sortingAlgorithms.mergeSort(this.state.array);*/
        if(array.length <= 1) return array;
        const middleIdx = Math.floor(array.length/2);
        const firstHalf = mergeSort(array.slice[0, middleIdx]);
        const secondHalf = mergeSort(array.slice[middleIdx]);
        const sortedArray = [];
        let i =0,
            j =0;

        while (i < firstHalf.length && j < secondHalf.length){
            if(firstHalf[i] < secondHalf[j]){
                sortedArray.push(firstHalf[i++]);
            }else{
                sortedArray.push(secondHalf[j++]);
            }
        }
        while (i < firstHalf.length) sortedArray.push(firstHalf[i++]);
        while (j < secondHalf.length) sortedArray.push(secondHalf[j++]);
        
    }

    quickSort(){}

    meapSort(){}

    bubbleSort(){}

    //open dropwdown
    showMenu(event) {
        event.preventDefault();
        
        if(this.state.showMenu){
            this.setState({
                showMenu: false,
              });
        }else{
            this.setState({
                showMenu: true,
              });
        }

        
      }

    render(){
        const {array} = this.state;

        return(
            <div className="array-container">
                <div className="menu">
                    <div><button onClick={()=>{this.resetArray()}} className="generateArray">New Array</button></div>
                    <div><button onClick={()=>{this.mergeSort()}} className="generateArray">New Array</button></div>
                    <div className="AlgoDropwDown">
                        <button onClick={this.showMenu} type="button">
                        Choose Algorithm
                        </button>
                        {
                        this.state.showMenu
                            ? (
                        <div className="dropdown-menu">
                            <button onClick={() => this.mergeSort()} type="button">Merge Sort</button>
                            <button onClick={() => {this.quickSort()}}>Quick Sort</button>
                            <button onClick={() => {this.meapSort()}}>Meap Sort</button>
                            <button onClick={() => {this.bubbleSort()}}>Bubble Sort</button>
                        </div>
                        )
                        : (
                          null
                        )}
                    </div>
                </div>
                {array.map((value, idx) => {
                    return <div className="array-bar" key={idx} style={{height: `${value}px`}}>

                    </div>
                })}
            </div>
        )
    }
}

function randomIntFromInterval(min, max){
    return Math.floor(Math.random() * (max - min + 1) + min);
}
function getWindowDimensions(whichOne) {
    const { innerWidth: width, innerHeight: height } = window;
    if(whichOne === 'height'){
        return height;
    }else{
        return width;
    }
  }
